import { useContext,useState,useEffect } from 'react';
import React from 'react';
import { set } from 'react-datepicker/dist/date_utils';
const AddEx = ({num,type,date}) => {
    //Dyamically increase num of excersises using prop from create
   
    const [name,setName] = useState(new Array(num).fill(""));
    const [weight,setWeight] = useState(new Array(num).fill(0));
    const [sets,setSets] = useState(new Array(num).fill(0));
    const [reps,setReps] = useState(new Array(num).fill(0));
    const [excersises,setExcersises] = useState(new Array(num).fill(0));
    const[isPending,setIsPending] = useState(false);

    const updateName = (index, newName) => {
        let copy = name;
        for(let i = 0; i < name.length; i++){
            if(i === index){
                copy[i] = newName;
            }
        }
        setName(copy);
      };
    const updateSets = (index, newSet) => {
        let copy = name;
        for(let i = 0; i < name.length; i++){
            if(i === index){
                copy[i] = newSet;
            }
        }
        setSets(copy);
      };
    const updateReps = (index, newReps) => {
    let copy = name;
    for(let i = 0; i < name.length; i++){
        if(i === index){
            copy[i] = newReps;
        }
        }
        setReps(copy);
        };
    const updateWeight = (index, newWeight) => {
        let copy = name;
        for(let i = 0; i < name.length; i++){
            if(i === index){
                copy[i] = newWeight;
            }
        }
        setWeight(copy);
        };
    
    const updateExcersises = () => {
        let copy = excersises;
        for(let i = 0; i < name.length; i++){
            copy[i]  = [name[i],weight[i],sets[i],reps[i]]
        }
        setExcersises(copy);

   };
    const handleSubmit = (e) => {
        e.preventDefault();
        updateExcersises();
        const workout = {type,date,num,excersises};
        setIsPending(true);
        console.log(workout);
        // fetch('http://localhost:8001/workouts',{
        //     method: 'POST',
        //     headers:{"Content-Type": "application/json"},
        //     body:JSON.stringify(workout)
        // }).then(() => {console.log('new workout ');
        //     setIsPending(false);
        //     navigate('/');

        // })
    }   
    return ( 
      <form onSubmit={handleSubmit}>
        <div className='excersises'  >
            <form action=""></form>
            <h2>Add your Excersises</h2>  
            {Array.from({ length: num }, (_, index) => (
                <div key={index} className='excersise'>
                    <h4>Excersise {index +1}</h4>  

                    <label > Excersise Name</label>
                        <input type="text" 
                        required 
                        value = {name}
                        onChange={(e) => updateName(index,e.target.value)} />
                        <br />

                    <label>Weight (lb)</label>
                        <input type="number" 
                        required value={ weight}
                        onChange={(e) => updateWeight(index,e.target.value)}/>
                        <br />

                    <label>Sets</label>
                        <input type="number" 
                        required value={ sets}
                        onChange={(e) => updateSets(index,e.target.value)}
                        />
                        <br />

                    <label>Reps per Set</label>
                        <input type="number" 
                        required value={ reps}
                        onChange={(e) => updateReps(index,e.target.value)}
                        />
                    
                </div>
        ))}
        {!isPending && <button >Add Workout</button>}
        {isPending && <button disabled>Adding Workout</button>} 

        
        </div>
      </form>
        
   
     );
}
 
export default AddEx;
