import React, { useState } from 'react';

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Link,useNavigate } from 'react-router-dom';

import AddEx from './addEx';


const Create = () => {
    const [type,setType] = useState('Push');
    const [date, setDate] = useState(null);
    const [num,setNum] = useState(3);
    const [excersises,setExcerises] = useState(null);
    const[isPending,setIsPending] = useState(false);
    const navigate = useNavigate();



    const handleSubmit = (e) => {
        e.preventDefault();
        const workout = {type,date,num,excersises};
        setIsPending(true);
        console.log(workout)
        fetch('http://localhost:8001/workouts',{
            method: 'POST',
            headers:{"Content-Type": "application/json"},
            body:JSON.stringify(workout)
        }).then(() => {console.log('new workout ');
            setIsPending(false);
            navigate('/');

        })
    }
    return ( 
        <div className='create'>
            <h2>Add a Workout</h2>  
            <form onSubmit={handleSubmit}>
                <label> Workout Type</label>
                <br />
                    <select
                        required
                        value= {type}
                        onChange={(e) => setType(e.target.value)}>
                        <option value="Push">Push</option>
                        <option value="Pull">Pull</option>
                        <option value="Legs">Legs</option>
                    </select>
                <br />
                <label>Date</label>
                <br />
                    <DatePicker selected={date}
                    onChange={(date) => setDate(date.getMonth + "/" + date.getDate + "/" +  date.getFullYear)}
                    required/>

                <br />
                <label>Number of Excersises</label>
                <br />
                    <input 
                    type="number"
                    required
                    value = {num}
                    onChange={(e) => setNum(e.target.value)}/>
                <br />
                <br />
                {setExcerises(AddEx.excersises)}
                {!isPending && <button>Add Workout</button>}
                {isPending && <button disabled>Adding Workout</button>}
                    
                
                
               </form>

        </div>
    );
}
 
export default Create;
