import { useContext,useState,useEffect } from 'react';
import React from 'react';
const AddEx = ({num , excersises}) => {
    //Dyamically increase num of excersises using prop from create
    const [name,setName] = useState([num]);
    const [sets,setSets] = useState([num]);
    const [reps,setReps] = useState([num]);
    const [weight,setWeight] = useState([num]);
        
    
    const putExcersises = (e) => {
        e.preventDefault();
        let x  = num;
        while(x > 0){
           excersises[x] = [name[x],weight[x],sets[x],reps[x]];
        } 
   };
    return ( 
        <div className='excersises'  >
            
            <h2>Add your Excersises</h2>  
            {Array.from({ length: num }, (_, index) => (
                <div key={index} className='excersise'>
                    <h4>Excersise {index +1}</h4>  

                    <label > Excersise Name</label>
                        <input type="text" 
                        required 
                        value = {name[index]}
                        onChange={(e) => setName(e.target.value)} />
                        <br />

                    <label>Weight (lb)</label>
                        <input type="number" 
                        required value={ weight[index]}
                        onChange={(e) => setWeight(e.target.value)}/>
                        <br />

                    <label>Sets</label>
                        <input type="number" 
                        required value={ sets[index]}
                        onChange={(e) => setSets(e.target.value)}
                        />
                        <br />

                    <label>Reps per Set</label>
                        <input type="number" 
                        required value={ reps[index]}
                        onChange={(e) => setReps(e.target.value)}
                        />
                    
                </div>
        ))}

   
        </div>
   
     );
}
 
export default AddEx;
