import { useNavigate, useParams } from "react-router-dom";
import useFetch from './useFetch';


const Ex = () => {
    const {id } = useParams()
    const {data:workout,error,isPending} = useFetch('http://localhost:8001/workouts/' + id);
    const navigate = useNavigate();

    const handleClick = ()=>{
        fetch('http://localhost:8001/workouts/' + workout.id,
            {
                method: 'DELETE'
            }
        ).then(() =>{
            navigate('/');
        })
    }
    
    return ( <div className="Excersises">
            {isPending && <div>Loading...</div>}
            {error && <div>{error} </div>}
           
            {workout && (
                
                <article>
                    <h2>{workout.date}</h2>
                    <h4> Workout type: {workout.type}</h4>
                    
                 
                    {workout.excersises.map(excersise =>(
                        <div className="excersises">
                            <h5>Excersise {excersise[0]}</h5>
                            <p>  
                            Weight (lbs): {excersise[1]} <br />
                            Sets: {excersise[2]} <br />
                            Reps per Set: {excersise[3]}</p>
                            
                        </div>
                    ))}
                        
                    
                    
                    
                    <button onClick={handleClick}>delete</button>
                </article>
        )}

    </div>);
}
 
export default Ex;
